import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Insights = ({ accessToken, pageId }) => {
    const [insights, setInsights] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [fancount, setFancount] = useState("");

    useEffect(() => {
        const fetchInsights = async () => {
            setLoading(true);
            try {
                // Fetch page access token first
                const pageTokenResponse = await axios.get(
                    `https://graph.facebook.com/v16.0/${pageId}?fields=access_token&access_token=${accessToken}`
                );
                const pageAccessToken = pageTokenResponse.data.access_token;

                // Fetch each metric separately
                //   const metrics = ['page_fan_adds_unique', 'page_views_total', 'page_post_engagements', 'page_fans'];
                const metrics = ['page_fans', 'page_views_total', 'page_post_engagements', 'page_impressions'];
                const insightsData = await Promise.all(metrics.map(async (metric) => {
                    try {
                        // const response = await axios.get(
                        //     `https://graph.facebook.com/v16.0/${pageId}/insights`,
                        //     {
                        //         params: {
                        //             metric: metric,
                        //             access_token: pageAccessToken,
                        //             period: 'day',
                        //             date_preset: 'last_30d'
                        //         }
                        //     }
                        // );
                        const response = await axios.get(
                            `https://graph.facebook.com/v16.0/${pageId}/insights`,
                            {
                              params: {
                                metric: metric,
                                access_token: pageAccessToken,
                                period: 'day',
                                date_preset: 'today'  // We only need the current value for page_fans
                              }
                            }
                          );
                        return response.data.data[0];
                    } catch (error) {
                        console.warn(`Failed to fetch ${metric}:`, error.response ? error.response.data : error);
                        return { name: metric, values: [{ value: 'N/A' }] };
                    }
                    
                }));

                setInsights(insightsData.filter(insight => insight !== null));
                setError(null);
            } catch (error) {
                console.error('Error fetching insights:', error.response ? JSON.stringify(error.response.data, null, 2) : error);
                setError(`Failed to fetch insights: ${error.response ? error.response.data.error.message : error.message}`);
                setInsights([]);
            } finally {
                setLoading(false);
            }
        };
        const fetchPageFans = async () => {
            try {
              const response = await axios.get(
                `https://graph.facebook.com/v16.0/${pageId}`,
                {
                  params: {
                    fields: 'fan_count',
                    access_token: accessToken
                  }
                }
              );
              console.log('Page fans:', response.data.fan_count);
              setFancount( response.data.fan_count)
            } catch (error) {
              console.error('Error fetching page fans:', error);
              return 'N/A';
            }
          };

        if (pageId && accessToken) {
            fetchInsights();
        }
    }, [accessToken, pageId]);

    if (loading) {
        return <div>Loading insights...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    // const getInsightValue = (metricName) => {
    //     const metric = insights.find(insight => insight && insight.name === metricName);
    //     return metric && metric.values && metric.values[0] ? metric.values[0].value : 'N/A';
    // };

    const getInsightValue = (metricName) => {
        const metric = insights.find(insight => insight && insight.name === metricName);
        if (!metric) {
          console.log(`Metric ${metricName} not found`);
          return 'N/A';
        }
        if (!metric.values || metric.values.length === 0) {
          console.log(`No values for metric ${metricName}`);
          return 'N/A';
        }
        const value = metric.values[0].value;
        if (value === null || value === undefined) {
          console.log(`Null or undefined value for metric ${metricName}`);
          return 'N/A';
        }
        // For 'page_fans', we usually just want the latest value
        if (metricName === 'page_fans') {
          return value;
        }
        // For other metrics, you might want to sum up values or handle differently
        return Array.isArray(value) ? value.reduce((sum, v) => sum + v, 0) : value;
      };

    return (
        <div style={{ display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap' }}>
            {/* <Card title="New Page Likes" value={getInsightValue('page_fan_adds_unique')} /> */}
            <Card title="Total Page Likes" value={getInsightValue('page_fans')} />
            <Card title="Total Page Views" value={getInsightValue('page_views_total')} />
            <Card title="Post Engagements" value={getInsightValue('page_post_engagements')} />
            <Card title="Total Page Fans" value={getInsightValue('page_fans')} />
        </div>
    );
};

const Card = ({ title, value }) => (
    <div style={{ border: '1px solid #ccc', borderRadius: '5px', padding: '10px', margin: '10px', minWidth: '200px' }}>
        <h3>{title}</h3>
        <p>{value}</p>
    </div>
);

export default Insights;
