import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Profile from './components/Profile';
import PageSelector from './components/PageSelector';
import Insights from './components/Insights';

const App = () => {
  const [accessToken, setAccessToken] = useState('');
  const [user, setUser] = useState(null);
  const [selectedPage, setSelectedPage] = useState(null);
  const [sinceDate, setSinceDate] = useState(null);
  const [untilDate, setUntilDate] = useState(null);

  const handleLogin = (token) => {
    setAccessToken(token);
  };

  useEffect(() => {
    const fetchUserProfile = async () => {
      if (!accessToken) return;

      try {
        const response = await fetch(
          `https://graph.facebook.com/v13.0/me?fields=name,email,picture&access_token=${accessToken}`
        );
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };

    fetchUserProfile();
  }, [accessToken]);

  const handlePageSelect = (pageId) => {
    setSelectedPage(pageId);
  };

  const handleDateRangeChange = (since, until) => {
    setSinceDate(since);
    setUntilDate(until);
  };

  return (
    <div className="App">
      {!accessToken ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <Profile user={user} />
          <PageSelector accessToken={accessToken} onPageSelect={handlePageSelect} />
          {selectedPage && (
            <Insights
              accessToken={accessToken}
              pageId={selectedPage}
              since={sinceDate}
              until={untilDate}
            />
          )}
          {/* Example: Date range selection component */}
          {/* <DateRangeSelector onChange={handleDateRangeChange} /> */}
        </>
      )}
    </div>
  );
};

export default App;
