import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Insights = ({ accessToken, pageId }) => {
  const [insights, setInsights] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInsights = async () => {
      try {
        // Fetch page access token first
        const pageTokenResponse = await axios.get(
          `https://graph.facebook.com/v13.0/${pageId}?fields=access_token&access_token=${accessToken}`
        );
        const pageAccessToken = pageTokenResponse.data.access_token;

        // Fetch each metric separately
        const metrics = ['page_fans_count', 'page_engaged_users', 'page_impressions', 'page_reactions_total'];
        const insightsData = await Promise.all(metrics.map(async (metric) => {
          try {
            const response = await axios.get(
              `https://graph.facebook.com/v13.0/${pageId}/insights`,
              {
                params: {
                  metric: metric,
                  access_token: pageAccessToken,
                  period: 'day',
                  date_preset: 'last_30d'
                }
              }
            );
            return response.data.data[0];
          } catch (error) {
            console.warn(`Failed to fetch ${metric}:`, error);
            return { name: metric, values: [{ value: 'N/A' }] };
          }
        }));

        setInsights(insightsData);
        setError(null);
      } catch (error) {
        console.error('Error fetching insights:', error);
        setError('Failed to fetch insights. Please check permissions and try again.');
      }
    };
    if (pageId && accessToken) {
      fetchInsights();
    }
  }, [accessToken, pageId]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!insights) {
    return <div>Loading insights...</div>;
  }

  const getInsightValue = (metricName) => {
    const metric = insights.find(insight => insight.name === metricName);
    return metric ? metric.values[0].value : 'N/A';
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap' }}>
      <Card title="Total Followers / Fans" value={getInsightValue('page_fans_count')} />
      <Card title="Total Engagement" value={getInsightValue('page_engaged_users')} />
      <Card title="Total Impressions" value={getInsightValue('page_impressions')} />
      <Card title="Total Reactions" value={getInsightValue('page_reactions_total')} />
    </div>
  );
};

const Card = ({ title, value }) => (
  <div style={{ border: '1px solid #ccc', borderRadius: '5px', padding: '10px', margin: '10px', minWidth: '200px' }}>
    <h3>{title}</h3>
    <p>{value}</p>
  </div>
);

export default Insights;
