import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Insights = ({ accessToken, pageId }) => {
  const [insights, setInsights] = useState(null);

  useEffect(() => {
    const fetchInsights = async () => {
      try {
        const response = await axios.get(
          `https://graph.facebook.com/v13.0/${pageId}/insights?metric=page_fans,page_engaged_users,page_impressions,page_reactions_total&access_token=${accessToken}`
        );
        setInsights(response.data.data);
      } catch (error) {
        console.error('Error fetching insights:', error);
      }
    };
    fetchInsights();
  }, [accessToken, pageId]);

  if (!insights) {
    return null;
  }

  return (
    <div>
      <h2>Page Insights:</h2>
      <div>
        <h3>Total Followers / Fans: {insights[0].values[0].value}</h3>
        <h3>Total Engagement: {insights[1].values[0].value}</h3>
        <h3>Total Impressions: {insights[2].values[0].value}</h3>
        <h3>Total Reactions: {insights[3].values[0].value}</h3>
      </div>
    </div>
  );
};

export default Insights;
